import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertUserSchema, insertCycleSchema, insertSymptomSchema, 
  insertMoodSchema, insertNutritionSchema, insertWellnessSchema,
  insertPregnancyMilestoneSchema, insertReminderSchema
} from "@shared/schema";
import { generateHealthInsights, answerHealthQuestion } from "./services/gemini";

export async function registerRoutes(app: Express): Promise<Server> {
  // Mock user ID for development (in production, use proper authentication)
  const MOCK_USER_ID = "user-1";

  // Create a mock user if it doesn't exist
  const initUser = async () => {
    const existingUser = await storage.getUser(MOCK_USER_ID);
    if (!existingUser) {
      console.log("Creating mock user...");
      const newUser = await storage.createUserWithId(MOCK_USER_ID, {
        username: "anna",
        email: "anna@example.com",
        password: "password",
        name: "Anna",
        dateOfBirth: "1990-05-15",
        isPregnant: false,
        pregnancyDueDate: null
      });
      console.log("Mock user created:", newUser);
    } else {
      console.log("Mock user already exists");
    }
  };

  // Initialize user on startup
  await initUser();

  // Users
  app.get("/api/user/profile", async (req, res) => {
    try {
      const user = await storage.getUser(MOCK_USER_ID);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ message: "Failed to get user profile" });
    }
  });

  app.put("/api/user/profile", async (req, res) => {
    try {
      const updates = req.body;
      const user = await storage.updateUser(MOCK_USER_ID, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ message: "Failed to update user profile" });
    }
  });

  // Cycles
  app.get("/api/cycles", async (req, res) => {
    try {
      const cycles = await storage.getCyclesByUserId(MOCK_USER_ID);
      res.json(cycles);
    } catch (error) {
      res.status(500).json({ message: "Failed to get cycles" });
    }
  });

  app.get("/api/cycles/current", async (req, res) => {
    try {
      const cycle = await storage.getCurrentCycle(MOCK_USER_ID);
      res.json(cycle);
    } catch (error) {
      res.status(500).json({ message: "Failed to get current cycle" });
    }
  });

  app.post("/api/cycles", async (req, res) => {
    try {
      const validatedData = insertCycleSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const cycle = await storage.createCycle(validatedData);
      res.json(cycle);
    } catch (error) {
      res.status(400).json({ message: "Invalid cycle data" });
    }
  });

  // Symptoms
  app.get("/api/symptoms", async (req, res) => {
    try {
      const { date } = req.query;
      const symptoms = await storage.getSymptomsByUserId(
        MOCK_USER_ID, 
        date as string
      );
      res.json(symptoms);
    } catch (error) {
      res.status(500).json({ message: "Failed to get symptoms" });
    }
  });

  app.post("/api/symptoms", async (req, res) => {
    try {
      const validatedData = insertSymptomSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const symptom = await storage.createSymptom(validatedData);
      res.json(symptom);
    } catch (error) {
      res.status(400).json({ message: "Invalid symptom data" });
    }
  });

  // Moods
  app.get("/api/moods", async (req, res) => {
    try {
      const { start, end } = req.query;
      const dateRange = start && end ? { start: start as string, end: end as string } : undefined;
      const moods = await storage.getMoodsByUserId(MOCK_USER_ID, dateRange);
      res.json(moods);
    } catch (error) {
      res.status(500).json({ message: "Failed to get moods" });
    }
  });

  app.post("/api/moods", async (req, res) => {
    try {
      const validatedData = insertMoodSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const mood = await storage.createMood(validatedData);
      res.json(mood);
    } catch (error) {
      res.status(400).json({ message: "Invalid mood data" });
    }
  });

  // Nutrition
  app.get("/api/nutrition", async (req, res) => {
    try {
      const { date } = req.query;
      const nutrition = await storage.getNutritionByUserId(
        MOCK_USER_ID, 
        date as string
      );
      res.json(nutrition);
    } catch (error) {
      res.status(500).json({ message: "Failed to get nutrition data" });
    }
  });

  app.post("/api/nutrition", async (req, res) => {
    try {
      const validatedData = insertNutritionSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const nutrition = await storage.createNutrition(validatedData);
      res.json(nutrition);
    } catch (error) {
      res.status(400).json({ message: "Invalid nutrition data" });
    }
  });

  // Wellness
  app.get("/api/wellness", async (req, res) => {
    try {
      const { start, end } = req.query;
      const dateRange = start && end ? { start: start as string, end: end as string } : undefined;
      const wellness = await storage.getWellnessByUserId(MOCK_USER_ID, dateRange);
      res.json(wellness);
    } catch (error) {
      res.status(500).json({ message: "Failed to get wellness data" });
    }
  });

  app.post("/api/wellness", async (req, res) => {
    try {
      const validatedData = insertWellnessSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const wellness = await storage.createWellness(validatedData);
      res.json(wellness);
    } catch (error) {
      res.status(400).json({ message: "Invalid wellness data" });
    }
  });

  // Pregnancy
  app.get("/api/pregnancy/milestones", async (req, res) => {
    try {
      const milestones = await storage.getPregnancyMilestonesByUserId(MOCK_USER_ID);
      res.json(milestones);
    } catch (error) {
      res.status(500).json({ message: "Failed to get pregnancy milestones" });
    }
  });

  app.post("/api/pregnancy/milestones", async (req, res) => {
    try {
      const validatedData = insertPregnancyMilestoneSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const milestone = await storage.createPregnancyMilestone(validatedData);
      res.json(milestone);
    } catch (error) {
      res.status(400).json({ message: "Invalid pregnancy milestone data" });
    }
  });

  // AI Insights
  app.get("/api/ai/insights", async (req, res) => {
    try {
      const { type } = req.query;
      const insights = await storage.getAiInsightsByUserId(
        MOCK_USER_ID, 
        type as string
      );
      res.json(insights);
    } catch (error) {
      res.status(500).json({ message: "Failed to get AI insights" });
    }
  });

  app.post("/api/ai/generate-insights", async (req, res) => {
    try {
      // Get user data for context
      const cycles = await storage.getCyclesByUserId(MOCK_USER_ID);
      const symptoms = await storage.getSymptomsByUserId(MOCK_USER_ID);
      const moods = await storage.getMoodsByUserId(MOCK_USER_ID);
      const wellness = await storage.getWellnessByUserId(MOCK_USER_ID);

      const insights = await generateHealthInsights({
        cycles,
        symptoms,
        moods,
        wellness
      });

      // Store insights
      for (const insight of insights) {
        await storage.createAiInsight({
          userId: MOCK_USER_ID,
          type: insight.type,
          content: insight.content,
          metadata: insight.metadata
        });
      }

      res.json(insights);
    } catch (error) {
      res.status(500).json({ message: "Failed to generate AI insights" });
    }
  });

  app.post("/api/ai/chat", async (req, res) => {
    try {
      const { question } = req.body;

      if (!question) {
        return res.status(400).json({ message: "Question is required" });
      }

      // Get user data for context
      const user = await storage.getUser(MOCK_USER_ID);
      const cycles = await storage.getCyclesByUserId(MOCK_USER_ID);
      const symptoms = await storage.getSymptomsByUserId(MOCK_USER_ID);
      const moods = await storage.getMoodsByUserId(MOCK_USER_ID);

      const answer = await answerHealthQuestion(question, {
        user,
        cycles,
        symptoms,
        moods
      });

      res.json({ answer });
    } catch (error) {
      res.status(500).json({ message: "Failed to get AI response" });
    }
  });

  // Reminders
  app.get("/api/reminders", async (req, res) => {
    try {
      const reminders = await storage.getRemindersByUserId(MOCK_USER_ID);
      res.json(reminders);
    } catch (error) {
      res.status(500).json({ message: "Failed to get reminders" });
    }
  });

  app.post("/api/reminders", async (req, res) => {
    try {
      const validatedData = insertReminderSchema.parse({
        ...req.body,
        userId: MOCK_USER_ID
      });
      const reminder = await storage.createReminder(validatedData);
      res.json(reminder);
    } catch (error) {
      res.status(400).json({ message: "Invalid reminder data" });
    }
  });

  // Dashboard stats
  app.get("/api/dashboard/stats", async (req, res) => {
    try {
      const today = new Date().toISOString().split('T')[0];
      const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];

      const [
        currentCycle,
        todayWellness,
        todaySymptoms,
        todayMood,
        weeklyWellness
      ] = await Promise.all([
        storage.getCurrentCycle(MOCK_USER_ID),
        storage.getWellnessByUserId(MOCK_USER_ID, { start: today, end: today }),
        storage.getSymptomsByUserId(MOCK_USER_ID, today),
        storage.getMoodsByUserId(MOCK_USER_ID, { start: today, end: today }),
        storage.getWellnessByUserId(MOCK_USER_ID, { start: weekAgo, end: today })
      ]);

      const stats = {
        cycleDay: currentCycle ? calculateCycleDay(currentCycle) : 0,
        steps: todayWellness[0]?.steps || 0,
        sleepHours: todayWellness[0]?.sleepHours || 0,
        mood: todayMood[0]?.mood || 'neutral',
        symptoms: todaySymptoms.map(s => s.type),
        weeklyProgress: {
          water: weeklyWellness.reduce((sum, w) => sum + (w.waterIntake || 0), 0),
          exercise: weeklyWellness.filter(w => (w.exerciseMinutes || 0) > 0).length,
          sleep: weeklyWellness.reduce((sum, w) => sum + (w.sleepHours || 0), 0)
        }
      };

      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to get dashboard stats" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

function calculateCycleDay(cycle: any): number {
  const today = new Date();
  const startDate = new Date(cycle.startDate);
  const diffTime = Math.abs(today.getTime() - startDate.getTime());
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  return diffDays;
}

server/storage.ts.content:
import { 
  type User, type InsertUser, type Cycle, type InsertCycle,
  type Symptom, type InsertSymptom, type Mood, type InsertMood,
  type Nutrition, type InsertNutrition, type Wellness, type InsertWellness,
  type PregnancyMilestone, type InsertPregnancyMilestone,
  type AiInsight, type InsertAiInsight, type Reminder, type InsertReminder
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createUserWithId(id: string, user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;

  // Cycles
  getCyclesByUserId(userId: string): Promise<Cycle[]>;
  getCurrentCycle(userId: string): Promise<Cycle | undefined>;
  createCycle(cycle: InsertCycle): Promise<Cycle>;
  updateCycle(id: string, updates: Partial<Cycle>): Promise<Cycle | undefined>;

  // Symptoms
  getSymptomsByUserId(userId: string, date?: string): Promise<Symptom[]>;
  createSymptom(symptom: InsertSymptom): Promise<Symptom>;
  updateSymptom(id: string, updates: Partial<Symptom>): Promise<Symptom | undefined>;

  // Moods
  getMoodsByUserId(userId: string, dateRange?: { start: string; end: string }): Promise<Mood[]>;
  createMood(mood: InsertMood): Promise<Mood>;
  updateMood(id: string, updates: Partial<Mood>): Promise<Mood | undefined>;

  // Nutrition
  getNutritionByUserId(userId: string, date?: string): Promise<Nutrition[]>;
  createNutrition(nutrition: InsertNutrition): Promise<Nutrition>;
  updateNutrition(id: string, updates: Partial<Nutrition>): Promise<Nutrition | undefined>;

  // Wellness
  getWellnessByUserId(userId: string, dateRange?: { start: string; end: string }): Promise<Wellness[]>;
  createWellness(wellness: InsertWellness): Promise<Wellness>;
  updateWellness(id: string, updates: Partial<Wellness>): Promise<Wellness | undefined>;

  // Pregnancy
  getPregnancyMilestonesByUserId(userId: string): Promise<PregnancyMilestone[]>;
  createPregnancyMilestone(milestone: InsertPregnancyMilestone): Promise<PregnancyMilestone>;
  updatePregnancyMilestone(id: string, updates: Partial<PregnancyMilestone>): Promise<PregnancyMilestone | undefined>;

  // AI Insights
  getAiInsightsByUserId(userId: string, type?: string): Promise<AiInsight[]>;
  createAiInsight(insight: InsertAiInsight): Promise<AiInsight>;

  // Reminders
  getRemindersByUserId(userId: string): Promise<Reminder[]>;
  createReminder(reminder: InsertReminder): Promise<Reminder>;
  updateReminder(id: string, updates: Partial<Reminder>): Promise<Reminder | undefined>;
  deleteReminder(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private cycles: Map<string, Cycle> = new Map();
  private symptoms: Map<string, Symptom> = new Map();
  private moods: Map<string, Mood> = new Map();
  private nutrition: Map<string, Nutrition> = new Map();
  private wellness: Map<string, Wellness> = new Map();
  private pregnancyMilestones: Map<string, PregnancyMilestone> = new Map();
  private aiInsights: Map<string, AiInsight> = new Map();
  private reminders: Map<string, Reminder> = new Map();

  // Users
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id, 
      createdAt: new Date(),
      dateOfBirth: insertUser.dateOfBirth ?? null,
      isPregnant: insertUser.isPregnant ?? false,
      pregnancyDueDate: insertUser.pregnancyDueDate ?? null
    };
    this.users.set(id, user);
    return user;
  }

  async createUserWithId(id: string, insertUser: InsertUser): Promise<User> {
    const user: User = { 
      ...insertUser, 
      id, 
      createdAt: new Date(),
      dateOfBirth: insertUser.dateOfBirth ?? null,
      isPregnant: insertUser.isPregnant ?? false,
      pregnancyDueDate: insertUser.pregnancyDueDate ?? null
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;
    const updated = { ...user, ...updates };
    this.users.set(id, updated);
    return updated;
  }

  // Cycles
  async getCyclesByUserId(userId: string): Promise<Cycle[]> {
    return Array.from(this.cycles.values())
      .filter(cycle => cycle.userId === userId)
      .sort((a, b) => new Date(b.startDate).getTime() - new Date(a.startDate).getTime());
  }

  async getCurrentCycle(userId: string): Promise<Cycle | undefined> {
    const cycles = await this.getCyclesByUserId(userId);
    return cycles.find(cycle => !cycle.endDate) || cycles[0];
  }

  async createCycle(insertCycle: InsertCycle): Promise<Cycle> {
    const id = randomUUID();
    const cycle: Cycle = { 
      ...insertCycle, 
      id, 
      createdAt: new Date(),
      endDate: insertCycle.endDate ?? null,
      length: insertCycle.length ?? null,
      flowIntensity: insertCycle.flowIntensity ?? null,
      ovulationDate: insertCycle.ovulationDate ?? null
    };
    this.cycles.set(id, cycle);
    return cycle;
  }

  async updateCycle(id: string, updates: Partial<Cycle>): Promise<Cycle | undefined> {
    const cycle = this.cycles.get(id);
    if (!cycle) return undefined;
    const updated = { ...cycle, ...updates };
    this.cycles.set(id, updated);
    return updated;
  }

  // Symptoms
  async getSymptomsByUserId(userId: string, date?: string): Promise<Symptom[]> {
    return Array.from(this.symptoms.values())
      .filter(symptom => 
        symptom.userId === userId && 
        (!date || symptom.date === date)
      )
      .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  }

  async createSymptom(insertSymptom: InsertSymptom): Promise<Symptom> {
    const id = randomUUID();
    const symptom: Symptom = { 
      ...insertSymptom, 
      id, 
      createdAt: new Date(),
      cycleId: insertSymptom.cycleId ?? null,
      notes: insertSymptom.notes ?? null
    };
    this.symptoms.set(id, symptom);
    return symptom;
  }

  async updateSymptom(id: string, updates: Partial<Symptom>): Promise<Symptom | undefined> {
    const symptom = this.symptoms.get(id);
    if (!symptom) return undefined;
    const updated = { ...symptom, ...updates };
    this.symptoms.set(id, updated);
    return updated;
  }

  // Moods
  async getMoodsByUserId(userId: string, dateRange?: { start: string; end: string }): Promise<Mood[]> {
    return Array.from(this.moods.values())
      .filter(mood => {
        if (mood.userId !== userId) return false;
        if (!dateRange) return true;
        const moodDate = new Date(mood.date);
        return moodDate >= new Date(dateRange.start) && moodDate <= new Date(dateRange.end);
      })
      .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  }

  async createMood(insertMood: InsertMood): Promise<Mood> {
    const id = randomUUID();
    const mood: Mood = { 
      ...insertMood, 
      id, 
      createdAt: new Date(),
      notes: insertMood.notes ?? null
    };
    this.moods.set(id, mood);
    return mood;
  }

  async updateMood(id: string, updates: Partial<Mood>): Promise<Mood | undefined> {
    const mood = this.moods.get(id);
    if (!mood) return undefined;
    const updated = { ...mood, ...updates };
    this.moods.set(id, updated);
    return updated;
  }

  // Nutrition
  async getNutritionByUserId(userId: string, date?: string): Promise<Nutrition[]> {
    return Array.from(this.nutrition.values())
      .filter(nutrition => 
        nutrition.userId === userId && 
        (!date || nutrition.date === date)
      )
      .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  }

  async createNutrition(insertNutrition: InsertNutrition): Promise<Nutrition> {
    const id = randomUUID();
    const nutrition: Nutrition = { 
      ...insertNutrition, 
      id, 
      createdAt: new Date(),
      notes: insertNutrition.notes ?? null,
      calories: insertNutrition.calories ?? null
    };
    this.nutrition.set(id, nutrition);
    return nutrition;
  }

  async updateNutrition(id: string, updates: Partial<Nutrition>): Promise<Nutrition | undefined> {
    const nutrition = this.nutrition.get(id);
    if (!nutrition) return undefined;
    const updated = { ...nutrition, ...updates };
    this.nutrition.set(id, updated);
    return updated;
  }

  // Wellness
  async getWellnessByUserId(userId: string, dateRange?: { start: string; end: string }): Promise<Wellness[]> {
    return Array.from(this.wellness.values())
      .filter(wellness => {
        if (wellness.userId !== userId) return false;
        if (!dateRange) return true;
        const wellnessDate = new Date(wellness.date);
        return wellnessDate >= new Date(dateRange.start) && wellnessDate <= new Date(dateRange.end);
      })
      .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  }

  async createWellness(insertWellness: InsertWellness): Promise<Wellness> {
    const id = randomUUID();
    const wellness: Wellness = { 
      ...insertWellness, 
      id, 
      createdAt: new Date(),
      steps: insertWellness.steps ?? null,
      waterIntake: insertWellness.waterIntake ?? null,
      sleepHours: insertWellness.sleepHours ?? null,
      sleepQuality: insertWellness.sleepQuality ?? null,
      exerciseMinutes: insertWellness.exerciseMinutes ?? null
    };
    this.wellness.set(id, wellness);
    return wellness;
  }

  async updateWellness(id: string, updates: Partial<Wellness>): Promise<Wellness | undefined> {
    const wellness = this.wellness.get(id);
    if (!wellness) return undefined;
    const updated = { ...wellness, ...updates };
    this.wellness.set(id, updated);
    return updated;
  }

  // Pregnancy
  async getPregnancyMilestonesByUserId(userId: string): Promise<PregnancyMilestone[]> {
    return Array.from(this.pregnancyMilestones.values())
      .filter(milestone => milestone.userId === userId)
      .sort((a, b) => b.week - a.week);
  }

  async createPregnancyMilestone(insertMilestone: InsertPregnancyMilestone): Promise<PregnancyMilestone> {
    const id = randomUUID();
    const milestone: PregnancyMilestone = { 
      ...insertMilestone, 
      id, 
      createdAt: new Date(),
      notes: insertMilestone.notes ?? null,
      weight: insertMilestone.weight ?? null,
      appointments: insertMilestone.appointments ?? null
    };
    this.pregnancyMilestones.set(id, milestone);
    return milestone;
  }

  async updatePregnancyMilestone(id: string, updates: Partial<PregnancyMilestone>): Promise<PregnancyMilestone | undefined> {
    const milestone = this.pregnancyMilestones.get(id);
    if (!milestone) return undefined;
    const updated = { ...milestone, ...updates };
    this.pregnancyMilestones.set(id, updated);
    return updated;
  }

  // AI Insights
  async getAiInsightsByUserId(userId: string, type?: string): Promise<AiInsight[]> {
    return Array.from(this.aiInsights.values())
      .filter(insight => 
        insight.userId === userId && 
        (!type || insight.type === type)
      )
      .sort((a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime());
  }

  async createAiInsight(insertInsight: InsertAiInsight): Promise<AiInsight> {
    const id = randomUUID();
    const insight: AiInsight = { 
      ...insertInsight, 
      id, 
      createdAt: new Date(),
      metadata: insertInsight.metadata ?? null
    };
    this.aiInsights.set(id, insight);
    return insight;
  }

  // Reminders
  async getRemindersByUserId(userId: string): Promise<Reminder[]> {
    return Array.from(this.reminders.values())
      .filter(reminder => reminder.userId === userId && reminder.isActive)
      .sort((a, b) => a.time.localeCompare(b.time));
  }

  async createReminder(insertReminder: InsertReminder): Promise<Reminder> {
    const id = randomUUID();
    const reminder: Reminder = { 
      ...insertReminder, 
      id, 
      createdAt: new Date(),
      isActive: insertReminder.isActive ?? true
    };
    this.reminders.set(id, reminder);
    return reminder;
  }

  async updateReminder(id: string, updates: Partial<Reminder>): Promise<Reminder | undefined> {
    const reminder = this.reminders.get(id);
    if (!reminder) return undefined;
    const updated = { ...reminder, ...updates };
    this.reminders.set(id, updated);
    return updated;
  }

  async deleteReminder(id: string): Promise<boolean> {
    return this.reminders.delete(id);
  }
}

export const storage = new MemStorage();

